import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Navbar from './Navbar';
import SignupModal from './SignupModal';
import LoginModal from './LoginModal';
import About from './About';
import Features from './Features';
import Dashboard from './Dashboard';

function App() {
  const [showSignup, setShowSignup] = useState(false);
  const [showLogin, setShowLogin] = useState(false);

  return (
    <Router>
      <div className="relative">
        {/* Navbar always visible */}
        <Navbar 
          onSignupClick={() => setShowSignup(true)} 
          onLoginClick={() => setShowLogin(true)} 
        />

        {/* Main Content */}
        <main className={`transition duration-300 ${(showSignup || showLogin) ? 'blur-sm' : ''}`}>
          <Routes>
            {/* Homepage */}
            <Route
              path="/"
              element={
                <section className="min-h-screen flex flex-col items-center justify-center bg-gradient-to-br from-purple-800 to-indigo-900 text-white px-6 text-center">
                  <h2 className="text-4xl font-bold mb-4">Invest in Crypto with Confidence</h2>
                  <p className="max-w-xl text-lg mb-6">
                    Join the leading platform for secure and profitable cryptocurrency investment opportunities.
                  </p>
                  <button
                    onClick={() => setShowSignup(true)}
                    className="bg-white text-purple-700 font-semibold px-6 py-3 rounded hover:bg-gray-100 transition"
                  >
                    Get Started
                  </button>
                </section>
              }
            />

            {/* Other Pages */}
            <Route path="/about" element={<About />} />
            <Route path="/features" element={<Features />} />

            {/* Dashboard */}
            <Route path="/dashboard" element={<Dashboard />} />
          </Routes>
        </main>

        {/* Modals */}
        {showSignup && <SignupModal onClose={() => setShowSignup(false)} />}
        {showLogin && <LoginModal onClose={() => setShowLogin(false)} />}
      </div>
    </Router>
  );
}

export default App;
