import React, { useState } from 'react';

export default function Navbar({ onSignupClick }) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav className="px-6 py-4">
      <div className="flex justify-between items-center">
        {/* Brand */}
        <h1 className="text-2xl font-bold text-indigo-700">CryptoInvest</h1>

        {/* Desktop Menu */}
        <ul className="hidden md:flex items-center space-x-6 font-medium text-gray-700">
          <li><a href="#about" className="hover:text-indigo-600">About Us</a></li>
          <li><a href="#features" className="hover:text-indigo-600">Features</a></li>
          <li><a href="#login" className="hover:text-indigo-600">Login</a></li>
          <li>
            <button
              onClick={onSignupClick}
              className="px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700 transition"
            >
              Sign Up
            </button>
          </li>
        </ul>

        {/* Hamburger Icon (Mobile Only) */}
        <button
          className="md:hidden text-indigo-700 focus:outline-none"
          onClick={() => setIsOpen(!isOpen)}
        >
          <svg
            className="w-8 h-8"
            fill="none"
            stroke="currentColor"
            viewBox="0 0 24 24"
            xmlns="http://www.w3.org/2000/svg"
          >
            {isOpen ? (
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M6 18L18 6M6 6l12 12"
              />
            ) : (
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2}
                d="M4 6h16M4 12h16M4 18h16"
              />
            )}
          </svg>
        </button>
      </div>

      {/* Mobile Menu (only shows when hamburger is clicked) */}
      {isOpen && (
        <div className="md:hidden mt-4 flex flex-col space-y-3 font-medium text-gray-700">
          <a href="#about" className="hover:text-indigo-600">About Us</a>
          <a href="#features" className="hover:text-indigo-600">Features</a>
          <a href="#login" className="hover:text-indigo-600">Login</a>
          <button
            onClick={onSignupClick}
            className="px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700 transition"
          >
            Sign Up
          </button>
        </div>
      )}
    </nav>
  );
}
