import React, { useState } from 'react';

export default function Navbar({ onSignupClick }) {
  const [isOpen, setIsOpen] = useState(false);

  return (
    <nav className="px-6 py-4">
      {/* Top Row */}
      <div className="flex justify-between items-center">
        <h1 className="text-2xl font-bold text-indigo-700">CryptoInvest</h1>
        <div className="flex items-center space-x-4">
          <a href="#login" className="hover:text-indigo-600 font-medium">Login</a>
          <button
            onClick={onSignupClick}
            className="px-4 py-2 bg-indigo-600 text-white rounded hover:bg-indigo-700 transition"
          >
            Sign Up
          </button>
          {/* Hamburger for mobile */}
          <button
            className="md:hidden text-indigo-700 focus:outline-none"
            onClick={() => setIsOpen(!isOpen)}
          >
            <svg
              className="w-8 h-8"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
              xmlns="http://www.w3.org/2000/svg"
            >
              {isOpen ? (
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M6 18L18 6M6 6l12 12"
                />
              ) : (
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth={2}
                  d="M4 6h16M4 12h16M4 18h16"
                />
              )}
            </svg>
          </button>
        </div>
      </div>

      {/* Bottom Row (Desktop Menu) */}
      <div className="mt-3 hidden md:flex justify-center space-x-8 text-gray-700 font-medium">
        <a href="#about" className="hover:text-indigo-600">About Us</a>
        <a href="#features" className="hover:text-indigo-600">Features</a>
        <a href="#pricing" className="hover:text-indigo-600">Pricing</a>
        <a href="#contact" className="hover:text-indigo-600">Contact</a>
      </div>

      {/* Mobile Menu */}
      {isOpen && (
        <div className="md:hidden mt-4 flex flex-col space-y-3 text-gray-700 font-medium">
          <a href="#about" className="hover:text-indigo-600">About Us</a>
          <a href="#features" className="hover:text-indigo-600">Features</a>
          <a href="#pricing" className="hover:text-indigo-600">Pricing</a>
          <a href="#contact" className="hover:text-indigo-600">Contact</a>
        </div>
      )}
    </nav>
  );
}
